      <script>
         (function() {
             if (typeof dataLayer === 'undefined') return;
             const headerMenu = document.querySelectorAll("ul[class*='header-menu'] > li");
             const hamburgerMenu = document.querySelectorAll("ul[class*='hamburger-menu'] > li");
         
             if (headerMenu.length === 0) return;
             if (hamburgerMenu.length === 0) return;
         
             [...headerMenu].map(q => {
                 const destination = q.querySelector("a").innerText.trim();
                 if (destination) {
                     try {
                         q.querySelector("a").addEventListener("click", function(evt) {
                             dataLayer.push({
                                 event: 'navigation',
                                 navigation: {
                                     menu: 'header',
                                     destination,
                                 }
                             });
                         });
                     } catch (e) {
                         console.log(e);
                     }
                 }
